using System.Collections; using System.Collections.Generic; using UnityEngine;  public partial class MyMesh : MonoBehaviour  {      GameObject[] mControllers;     GameObject[] mNormal;      public void InitControllers()     {         ClearControllers();          Mesh theMesh = GetComponent<MeshFilter>().mesh;         Vector3[] v = theMesh.vertices;          mControllers = new GameObject[v.Length];         mNormal = new GameObject[v.Length];         for (int i = 0; i < v.Length; i++)         {             //sphere creation             mControllers[i] = GameObject.CreatePrimitive(PrimitiveType.Sphere);             mControllers[i].transform.localScale = new Vector3(0.1f, 0.1f, 0.1f);             mControllers[i].AddComponent<VertexController>();              mControllers[i].transform.localPosition = v[i];             mControllers[i].transform.parent = this.transform;              //normal creation             mNormal[i] = GameObject.CreatePrimitive(PrimitiveType.Cylinder);             mNormal[i].transform.localScale = new Vector3(0.005f, 0.2f, 0.005f);             mNormal[i].layer = LayerMask.NameToLayer("Ignore Raycast");              Vector3 normalPosition = v[i];             normalPosition.y += 0.25f;             mNormal[i].transform.localPosition = normalPosition;             mNormal[i].transform.parent = mControllers[i].transform;         }         manipulationIsOff = false;     }      public void ClearControllers()     {         if (mControllers != null){             foreach (GameObject g in mControllers) Destroy(g);             manipulationIsOff = true;         }     } }